import { readFileSync, writeFileSync } from 'fs';

// Read the RTL plugin code from node_modules
const pluginCode = readFileSync('node_modules/@mapbox/mapbox-gl-rtl-text/dist/mapbox-gl-rtl-text.js', 'utf8');

// Generate a TypeScript file that exports the plugin code as a string
const output = `// This file is auto-generated by scripts/generate-rtl-plugin.mjs
// Do not edit manually

export const rtlPluginCode = ${JSON.stringify(pluginCode)};
`;

writeFileSync('src/map/rtl-plugin-code.ts', output);

